export default function Menu() {
  const router = useRouter();
  const Links = links.map((link, i) => (
    <Link key={i} href={link.href}>
      <a>
        <li
          className={link.href.includes(router.pathname.split("/")[1]) && router.pathname !== "/" ? styles.active : ""}
        >
          <div className={styles.wrap}>
            {link.icon}
            <span className={styles.linkName}>{link.name}</span>
          </div>
          {link.notification === true ? (
            <div className={styles.wrapNotification}>
              <Notifications />
            </div>
          ) : (
            ""
          )}
        </li>
      </a>
    </Link>
  ));
  return (
    <React.Fragment>
      <nav className={styles.nav}>
        <Link href="/">
          <a className={styles.crm_logo}>{<CrmLogo />}</a>
        </Link>
        <div className={styles.menu}>
          <ul>{Links}</ul>
        </div>
      </nav>
    </React.Fragment>
  );
}
